files.content:
@props([
    'defaultValue' => null,
    'type' => 'single',
    'class' => '',
    'keepOneOpen' => false,
    'multiple' => false,
])

<div x-accordion @if ($defaultValue && $defaultValue != '') data-default-value="{{ $defaultValue }}" @endif
    @if ($keepOneOpen) data-prevent-closing-all @endif data-accordion-type="{{ $multiple ? 'multiple' : $type }}"
    {{ $attributes->class([$class]) }}>
    {{ $slot }}
</div>

@props(['variant' => 'default', 'intent' => 'gray', 'id', 'class' => null])

@php
    use App\Flexiwind\UiHelper;
    $variants = UiHelper::getVariants();

    $className = '';
    $variantClass = UiHelper::getClasses($variant, $intent);
    if ($variant === 'default') {
        $className = ' border-b border-border ';
    }

    $className .= "{$class} {$variantClass}";

@endphp

<div data-accordion-item data-accordion-value="{{ $id }}" class="{{ $className }}">
    {{ $slot }}
</div>

@props(['class'=>''])
<button data-accordion-trigger aria-label="toggle button"
    class="w-full flex justify-between items-center {{ $class }}">
    {{ $slot }}
</button>

@props(['style', 'icon' => 'ph--caret-down'])

<span data-trigger-icon
    class="ease-linear duration-300 iconify ph--caret-down in-[[aria-expanded=true]]:rotate-180 text-sm"></span>
