@props([
    'id',
    'class' => '',
    'contentClass' => '',
    'placement' => 'bottom',
    'keepOpen' => false,
    'keppOpenOut' => false,
    'isSubMenu' => false,
])

@php
    $finaPosition = $isSubMenu ? (in_array($placement, ['right', 'right-start', 'right-middle', 'right-end']) ? $placement : 'right-start') : $placement;
@endphp

<div x-data x-dropdown wire:ignore.self role="list" id="dropdown-{{ $id }}" data-drop-down
    @if ($keepOpen) data-prevent-close-inside @endif data-placement="{{ $finaPosition }}"
    @if ($keppOpenOut) data-prevent-close-outside @endif
    {{ $attributes->class([
        'opacity-0 invisible translate-y-3 fx-open:opacity-100 fx-open:visible fx-open:translate-y-0 text-sm bg-bg border border-border shadow-sm rounded-lg flex flex-col overflow-hidden text-fg ease-linear transition-transform',
        'ui-popper [--dropdown-padding:--spacing(1)] [--dropdown-radius:var(--radius-lg)] z-40',
        $class,
    ]) }}>
    <div
        class="dropdown-content rounded-(--dropdown-radius) p-(--dropdown-padding) text-left gap-0.5 {{ $contentClass }}">
        {{ $slot }}
    </div>
</div>

@props(['danger' => false, 'href' => null, 'disabled' => false])
@php
    $itemClassDefault = $danger ? 'dropdown-item-bg-danger' : 'dropdown-item-bg';
    $className = ' ';

    $attributes = $attributes->class([
        '[--mr-icon:--spacing(2)]',
        'group relative select-none dropdown-item-base dropdown-item-grid text-left',
        'dropdown-item-slot dropdown-item-icon dropdown-item-label ',
        'dropdown-item-force-color',
        'dropdown-item-danger',
        'cursor-default text-base/6 sm:text-sm/6',
        $itemClassDefault,
        'cursor-not-allowed' => $disabled,
    ]);

    $tag = $href ? 'a' : 'button';
    $attributes =
        $tag === 'button'
            ? ($attributes = $attributes->merge([
                'disabled' => $disabled,
            ]))
            : $attributes->merge([
                'href' => $disabled ? null : $href,
                'aria-disabled' => $disabled ? 'true' : null,
                'tabindex' => $disabled ? '-1' : null,
            ]);
@endphp

<{{ $tag }} {{ $attributes }}>
    {{ $slot }}
    </{{ $tag }}>

@props(['dropdownId', 'variant' => 'none', 'disabled' => false])
@php

    $attributes = $attributes->class([
        '[--mr-icon:--spacing(2)]',
        'group relative select-none dropdown-item-base dropdown-item-bg dropdown-item-grid text-left',
        'dropdown-item-slot dropdown-item-icon dropdown-item-label ',
        'dropdown-item-force-color',
        'dropdown-item-danger',
        'cursor-default text-base/6 sm:text-sm/6',
        'cursor-not-allowed' => $disabled,
    ]);

    $attributes = $attributes = $attributes->merge([
        'disabled' => $disabled,
    ]);
@endphp

<button data-dropdown-trigger data-dropdown-id="dropdown-{{ $dropdownId }}" {{ $attributes }}>
    {{ $slot }}
    <span data-slot="chevron" class="flex iconify ph--caret-right absolute right-2 text-xs"></span>
</button>
