@props([
    'modalId',
    'backdropClass' => '',
    'overlayBlured' => true,
    'closable' => true,
    'staticBackdrop' => '',
    'scrollableBody' => false,
])

@php
    $bluredClass = $overlayBlured ? 'backdrop-blur-xs' : '';
    $overlay_class = "fixed inset-0 bg-gray-800/60 {$bluredClass} {$backdropClass}";
@endphp
<dialog x-modal data-modal-id="{{ $modalId }}" wire:ignore.self
    @if ($staticBackdrop) data-modal-prevent @endif
    @if ($scrollableBody) data-allow-body-scroll @endif
    {{ $attributes->class([
        'fixed top-0 left-0 w-screen h-screen z-80 hidden fx-open:flex items-center justify-center bg-transparent',
    ]) }}>
    <span data-modal-overlay wire:ignore class="{{ $overlay_class }}"></span>
    {{ $slot }}
</dialog>

@props(['size' => 'md', 'class' => '', 'closable' => true, 'noGutter' => false])

@php
    $sizes = [
        'xs' => 'sm:max-w-sm',
        'sm' => 'sm:max-w-md',
        'md' => 'sm:max-w-lg',
        'lg' => 'sm:max-w-xl',
        'xl' => 'sm:max-w-2xl',
        '2xl' => 'sm:max-w-3xl',
        '3xl' => 'sm:max-w-4xl',
        '4xl' => 'sm:max-w-5xl',
        'full' => 'max-w-full',
    ];

    $size_ = $sizes[$size] ?? $sizes['lg'];

@endphp

<div data-modal-content wire:ignore.self
    {{ $attributes->class([
        'w-full flex flex-col relative bg-bg border border-border shadow-sm rounded-lg overflow-hidden',
        '[--gutter:--spacing(6)] sm:[--gutter:--spacing(8)]' => !$noGutter,
        $size_,
        $class,
    ]) }}>
    {{ $slot }}

    @if ($closable)
        <x-ui.modal.close size="icon-sm" variant="none" intent="none"
            class="absolute top-4 right-4 hover:bg-bg-muted rounded-full text-fg z-90">
            <span aria-hidden="true" class="flex iconify ph--x"></span>
        </x-ui.modal.close>
    @endif
</div>

@props([
    'title' => null,
    'description' => null,
    'class' => '',
    'titleClass' => '',
    'descriptionClass' => '',
    'paddingNone' => false,
])

@php
    $title_class = $titleClass;
    $description_class = $descriptionClass;
    $className = '';
@endphp


<div data-slot="sheet-header"
    {{ $attributes->class([
        'relative flex items-start gap-3.5 relative',
        'p-(--gutter) pb-[calc(var(--gutter)---spacing(3)) space-y-1' => !$paddingNone,
        $class,
    ]) }}>
    @if (isset($title) || isset($titleSlot))
        <div class="flex-1 space-y-1 pr-14">
            <x-ui.modal.title class="{{ $title_class }}">
                @if (isset($title))
                    {{ $title }}
                @else
                    {{ $titleSlot }}
                @endif
                </x-ui.smodal.title>
                @if (isset($description) || isset($descriptionSlot))
                    <x-ui.modal.description class="{{ $description_class }}">
                        @if (isset($description))
                            {{ $description }}
                        @else
                            {{ $descriptionSlot }}
                        @endif
                    </x-ui.modal.description>
                @endif
        </div>
    @else
        {{ $slot }}
    @endif
</div>
